import {Component} from "react"
import { IoMdArrowDropup } from "react-icons/io";
import { IoIosArrowUp } from "react-icons/io";
import { IoIosArrowDown } from "react-icons/io";


import "./app.css"


import {PageContainer,Container,Input,Form,HeadingText,SelectOptions,IconsContainer,FontSizeCard,FontSizeText,Button,
    InputColor,FontSizeAndInputCard,ColorCard,HeadingText1} from "./styledComponent"

const fontFamilies = [
    {
      name: "Arial",
      category: "Sans-serif",
      uniqueNo: "Arial",  
    },
    {
      name: "Verdana",
      category: "Sans-serif",
      uniqueNo: "Verdana",
    },
    {
      name: "Times New Roman",
      category: "Serif",
      uniqueNo: "Times New Roman",
    },
    {
      name: "Georgia",
      category: "Serif",
      uniqueNo: "Georgia",
    },
    {
      name: "Courier New",
      category: "Monospace",
      uniqueNo: "Courier New",
    },
    {
      name: "Comic Sans MS",
      category: "Cursive",
      uniqueNo: "Comic Sans MS",
    },
   ];
 

class App extends Component {

    render(){
        return(
            <PageContainer>
                <Container>
                    <Input type="text"  />
                </Container>
                <Form>
                    <HeadingText htmlFor = "fontinput">Font Style</HeadingText>
                    <SelectOptions>
                        {fontFamilies.map((family) => <option key = {family.uniqueNo}>{family.name}</option>)}
                    </SelectOptions>
                    
                    <FontSizeAndInputCard>
                        <ColorCard>
                            <HeadingText1>Size</HeadingText1>
                            <FontSizeCard>
                                <FontSizeText>16</FontSizeText>
                                <IconsContainer>
                                    <Button type="button">
                                    <IoIosArrowUp className="icon"/>
                                    </Button>                        
                                    <Button type="button"> 
                                    <IoIosArrowDown className="icon"/>
                                    </Button>
                                </IconsContainer>
                            </FontSizeCard>
                        </ColorCard>
                        <ColorCard>
                            <HeadingText>Color</HeadingText>
                            <InputColor type = "color" />
                        </ColorCard>
                     </FontSizeAndInputCard>
                </Form>
            </PageContainer>
        )
    }
}

 

export default App
