import {Component} from "react"
import { IoMdArrowDropup } from "react-icons/io";
import { IoIosArrowUp } from "react-icons/io";
import { IoIosArrowDown } from "react-icons/io";


import "./app.css"


import {PageContainer,Container,Input,Form,HeadingText,SelectOptions,IconsContainer,FontSizeCard,FontSizeText,Button,
    InputColor,FontSizeAndInputCard,ColorCard,HeadingText1,AddText,Option} from "./styledComponent"

const fontFamilies = [
    {
      name: "Arial",
      category: "Sans-serif",
      uniqueNo: "Arial",  
    },
    {
      name: "Verdana",
      category: "Sans-serif",
      uniqueNo: "Verdana",
    },
    {
      name: "Times New Roman",
      category: "Serif",
      uniqueNo: "Times New Roman",
    },
    {
      name: "Georgia",
      category: "Serif",
      uniqueNo: "Georgia",
    },
    {
      name: "Courier New",
      category: "Monospace",
      uniqueNo: "Courier New",
    },
    {
      name: "Comic Sans MS",
      category: "Cursive",
      uniqueNo: "Comic Sans MS",
    },
    {
        name:"Roboto",
        uniqueNo:"Roboto"
    }
   ];
 

class App extends Component {

    state = {fontStyle:fontFamilies[0].name}

    onChangeFontStyle = (event) => {
        console.log(event.target.value)
        this.setState({fontStyle:event.target.value})
    }

    render(){
        const fontStyle = this.state
        

        return(
            <PageContainer>
                <Container>
                    <Input type="text"  placeholder="New Text" />
                </Container>
                <Form>
                    <HeadingText htmlFor = "fontinput">Font Style</HeadingText>
                    <SelectOptions onChange={this.onChangeFontStyle} value={fontStyle}>
                        {fontFamilies.map((family) => <Option key = {family.uniqueNo}>{family.name}</Option>)}
                    </SelectOptions>
                    
                    <FontSizeAndInputCard>
                        <ColorCard>
                            <HeadingText1>Size</HeadingText1>
                            <FontSizeCard>
                                <FontSizeText>16</FontSizeText>
                                <IconsContainer>
                                    <Button type="button">
                                    <IoIosArrowUp className="icon"/>
                                    </Button>                        
                                    <Button type="button"> 
                                    <IoIosArrowDown className="icon"/>
                                    </Button>
                                </IconsContainer>
                            </FontSizeCard>
                        </ColorCard>
                        <ColorCard>
                            <HeadingText>Color</HeadingText>
                            <InputColor type = "color" />
                        </ColorCard>
                     </FontSizeAndInputCard>
                     <AddText  type = "button">
                        Add Text
                     </AddText>
                </Form>
            </PageContainer>
        )
    }
}

 

export default App
