import {Component} from "react"
import { IoMdArrowDropup } from "react-icons/io";


import {PageContainer,Container,Input,Form,LabelText,SelectOptions,IconsContainer} from "./styledComponent"

const fontFamilies = [
    {
      name: "Arial",
      category: "Sans-serif",
      uniqueNo: "Arial",  
    },
    {
      name: "Verdana",
      category: "Sans-serif",
      uniqueNo: "Verdana",
    },
    {
      name: "Times New Roman",
      category: "Serif",
      uniqueNo: "Times New Roman",
    },
    {
      name: "Georgia",
      category: "Serif",
      uniqueNo: "Georgia",
    },
    {
      name: "Courier New",
      category: "Monospace",
      uniqueNo: "Courier New",
    },
    {
      name: "Comic Sans MS",
      category: "Cursive",
      uniqueNo: "Comic Sans MS",
    },
   ];
 

class App extends Component {

    render(){
        return(
            <PageContainer>
                <Container>
                    <Input type="text"  />
                </Container>
                <Form>
                    <LabelText htmlFor = "fontinput">Font Style</LabelText>
                    <SelectOptions>
                        {fontFamilies.map((family) => <option>{family.name}</option>)}
                        <LabelText>Size</LabelText>
                        <div>
                            <p>16</p>
                            <IconsContainer>
                                <IoMdArrowDropup className="icon"/>
                            </IconsContainer>
                        </div>
                    </SelectOptions>
                </Form>
            </PageContainer>
        )
    }
}

 

export default App
