import {Component} from "react"
import {PageContainer,Container,Input,Form,LabelText,SelectOptions} from "./styledComponent"


 
const fontFamilies = [
  {
    name: "Arial",
    category: "Sans-serif",
    uniqueNo: "Arial", // Use font face name for consistency
  },
  {
    name: "Verdana",
    category: "Sans-serif",
    uniqueNo: "Verdana",
  },
  {
    name: "Times New Roman",
    category: "Serif",
    uniqueNo: "Times New Roman",
  },
  {
    name: "Georgia",
    category: "Serif",
    uniqueNo: "Georgia",
  },
  {
    name: "Courier New",
    category: "Monospace",
    uniqueNo: "Courier New",
  },
  {
    name: "Comic Sans MS",
    category: "Cursive",
    uniqueNo: "Comic Sans MS",
  },
 ];
 
class App extends Component {

    render(){
        return(
            <PageContainer>
                <Container>
                    <Input type="text"  />
                </Container>
                <Form>
                    <LabelText htmlFor = "fontinput">Font Style</LabelText>
                    <SelectOptions>
                        <option>hh</option>
                        <option>jhaj</option>
                    </SelectOptions>
                </Form>
            </PageContainer>
        )
    }
}

 

export default App
